import React from "react";
//import styles from "./Favorites.module.css";
import { useSelector , useDispatch } from "react-redux";
import Card from "../Card/Card";
import style from "./Favorites.module.css";
import action, { filterCards, orderCards } from "../../redux/action"

const Favorites = () => {
  const favorites = useSelector((state) => state.myFavorites);
  const dispatch = useDispatch();

  const handleOrder = (e) => {
    const action = orderCards(e.target.value); // Crea la acción que quieres despachar
    dispatch(action); // Dispatch de la acción
  };
  const handleFilter = (e) => {
    const action = filterCards(e.target.value); // Crea la acción que quieres despachar
    dispatch(action); // Dispatch de la acción
  };

  const [aux, setAux] = useState(0);

  return (
    <div className={style.container}>
      {favorites.map((char) => {
        return (
          <>
          <select onChange={handleOrder}>
            <option value="A">Ascendente</option>
            <option value="D">Descendente</option>
          </select>
          <select onChange={handleFilter}>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Genderless">Genderless</option>
            <option value="unknown">unknown</option>
          </select>
          <Card
              key={char.id}
              id={char.id}
              name={char.name}
              status={char.status}
              species={char.species}
              gender={char.gender}
              origin={char.origin.name}
              image={char.image} /></>
        );
      })}
    </div>
  );
};

export default Favorites;
