const validation = (userData, errors, setErrors) => {
  const emailRegex = /^[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,}$/;
  if (!userData.email) {
    setErrors({ ...errors, email: "Falta completar este campo" });
  } else if (!emailRegex.test(userData.email)) {
    setErrors({ ...errors, email: "Este no es un email valido" });
  } else if (userData.email > 35) {
    setErrors({
      ...errors,
      email: "No está permitido emails de más de 35 caracteres.",
    });
  } else if (!/\d+/.test(userData.password)) {
    setErrors({
      ...errors,
      password: "Falta ingresar al menos un numero en la contraseña",
    });
  } else if (!(userData.password > 6 && userData.password < 10)) {
    setErrors({
      ...errors,
      password:
        "La contraseña no cumple con la longitud de entre 6 y 10 caracteres",
    });
  } else {
    setErrors({ ...errors, email: "Este email es valido" });
  }
};

export default validation;
