const validation = (data) => {
    let errors = {
        email: [],
        pass: []
    };
    if(!data.email.includes("@")){
        errors.email.push("Ingresa un email valido.")
    }
    if(!data.email) {
        errors.email.push("Ingresa un email.")
    }
    if(data.email.length >35) {
        errors.email.push("Menos de 35 caracteres.")
    }
    if(!(/.*\d+.*/.test(data.password))) {
        errors.pass.push("Al menos un numero")
    }
    if(data.password.length < 6 || data.password.length > 10) {
        errors.p2 = "Longitud incorrecta"
    }
    return errors
}
export default validation
