import { ADD_FAV, REMOVE_FAV } from "./action-type";

const initialState = {
  myFavorites: [], // = [{character1} , {character2} , {character3}]
};
function eliminarCharacterPorId(myFavorites, id) {
  const newFavorites = myFavorites.filter((character) => character.id !== Number(id));

  return newFavorites;
}

const reducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case ADD_FAV:
      return { ...state, myFavorites: [...state.myFavorites, ] };
    case REMOVE_FAV:
      return {
        ...state,
        myFavorites: eliminarCharacterPorId(state.myFavorites, payload),
      };
    default:
      return state;
  }
};

export default reducer;
