import { ADD_FAV, FILTER, REMOVE_FAV, ORDER } from "./action-type";

const initialState = {
  myFavorites: [], // = [{character1} , {character2} , {character3}]
  allCharacters: [],
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_FAV:
      let copy
      return {
        ...state,
        allCharacters: [...state.allCharacters, action.payload],
      };
    case REMOVE_FAV:
      return {
        ...state,
        myFavorites: state.myFavorites.filter(
          (char) => char.id !== action.payload
        ),
      };
    case FILTER:
      let copy2 = state.allCharacters.filter(
        (character) => character.gender === action.payload
      );
      return { ...state, myFavorites: copy2 };
    case ORDER:
      let copy3 = state.allCharacters;
      if (action.payload === "A") {
        copy3.sort((a, b) => a.id - b.id);
      } else {
        copy3.sort((a, b) => b.id - a.id);
      }
      return { ...state, myFavorites: copy3 };
    default:
      return { ...state };
  }
};

export default rootReducer;
