import { ADD_FAV, REMOVE_FAV } from "./action-type";

const initialState = {
  myFavorites: [], // = [{character1} , {character2} , {character3}]
};
function eliminarCharacterPorId(myFavorites, id) {
  const newFavorites = myFavorites.filter(
    (character) => character.id !== Number(id)
  );

  return newFavorites;
}

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_FAV:
      return { ...state, myFavorites: [...state.myFavorites, action.payload] };
    case REMOVE_FAV:
      return {...state, myFavorites: state.myFavorites};
    default:
      return {...state};
  }
};

export default rootReducer;
