import { ADD_FAV, FILTER, REMOVE_FAV, ORDER } from "./action-type";

const initialState = {
  myFavorites: [], // = [{character1} , {character2} , {character3}]
  allCharacters: [],
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_FAV:
      let copyAllCharacter = state.allCharacters;
      return {
        ...state,
        myFavorites: [...copyAllCharacter, action.payload],
        allCharacters: copyAllCharacter,
      };
    case REMOVE_FAV:
      return {
        ...state,
        myFavorites: state.myFavorites.filter(
          (char) => char.id !== action.payload
        ),
      };
    case FILTER:
      let copy2 = state.allCharacters.filter(
        (character) => character.gender === action.payload
      );
      return { ...state, myFavorites: copy2 };
    case ORDER:
      const copy3 = state.allCharacters.sort((a, b) => {
        if (action.payload === "A"){
          if (a.id<b.id) return -1;
          if (a.id>b.id) return 1;
          return 0
        }
      });
      return { ...state, myFavorites: copy3 };
    default:
      return { ...state };
  }
};

export default rootReducer;
