import { Link } from "react-router-dom";
import style from "./Card.module.css";
import { connect } from 'react-redux'
import { addFav, removeFav } from "../../redux/action";
import { useState } from "react";


//hola
export function Card(props) {
   const {id, name, status, species, gender, origin, image, onClose, addFav, removeFav} = props
   const [isFav, setIsFav] = useState(false);

   const handleFavorite = () => { 
      isFav ? removeFav(id) : addFav(props);
      setIsFav(!isFav)
    }

    return (

      <div>
         <div className={style.conteiner}>
         {
         isFav ? (
            <button onClick={handleFavorite}>‚ù§Ô∏è</button>
         ) : (
            <button onClick={handleFavorite}>ü§ç</button>
         )
      }
            <div className={style.conteiner2}>
            <button className={style.button} onClick={() => {onClose(id)}}>
             X
            </button>
            <img className={style.img} src={image} alt={`${name}`}/>
            <Link to={`/detail/${id}`}>
            <h2 className={style.name}>{name}</h2>
            </Link>
            </div>
            <div className={style.datos}>
            <h3>{status}</h3>
            <h3>{species}</h3>
            <h3>{gender}</h3>
            <h3>{origin.name}</h3>
            </div>
            </div>
       </div>
    );
}



const mapStateToProps = (state) => ({})
//esta funcion recibe como parametro la funcion dispatch que a su vez recibe como parametro una funcion que crea una 
//action que estan definidas en el archivo action.js
const mapDispatchToProps = (dispatch) => {
   return{
      addFav: (character) => { 
         dispatch(addFav(character))
       },
      removeFav: (id) => { 
            dispatch(removeFav(id))
       }
   }
}

export default connect(null, mapDispatchToProps)(Card)
